package cn.com.cdboost.collect.cache;

import java.io.Serializable;

/**
 * 设备缓存信息
 */
public class DeviceCacheVo implements Serializable{
    /**
     * 设备唯一标识(生成规则:设备类型+补0(不足30位)+设备编号）
     */
    private String cno;

    /**
     * 设备编号
     */
    private String deviceNo;

    /**
     * 设备类型(01-LoRa网管，02-LoRa节点，04-集中器，05-采集器，07-电表 08 标识水表 09 标识气表)
     */
    private String deviceType;

    /**
     * 上级设备编号
     */
    private String pDeviceNo;

    /**
     * 安装位置
     */
    private String installAddr;

    /**
     * 所属组织机构
     */
    private Long orgNo;

    /**
     * 状态依赖设备编号
     */
    private String relyCno;

    /**
     * 是否重点表
     */
    private Integer isImportant;

    public String getCno() {
        return cno;
    }

    public void setCno(String cno) {
        this.cno = cno;
    }

    public String getDeviceNo() {
        return deviceNo;
    }

    public void setDeviceNo(String deviceNo) {
        this.deviceNo = deviceNo;
    }

    public String getDeviceType() {
        return deviceType;
    }

    public void setDeviceType(String deviceType) {
        this.deviceType = deviceType;
    }

    public String getpDeviceNo() {
        return pDeviceNo;
    }

    public void setpDeviceNo(String pDeviceNo) {
        this.pDeviceNo = pDeviceNo;
    }

    public String getInstallAddr() {
        return installAddr;
    }

    public void setInstallAddr(String installAddr) {
        this.installAddr = installAddr;
    }

    public Long getOrgNo() {
        return orgNo;
    }

    public void setOrgNo(Long orgNo) {
        this.orgNo = orgNo;
    }

    public String getRelyCno() {
        return relyCno;
    }

    public void setRelyCno(String relyCno) {
        this.relyCno = relyCno;
    }

    public Integer getIsImportant() {
        return isImportant;
    }

    public void setIsImportant(Integer isImportant) {
        this.isImportant = isImportant;
    }
}
